import { Component, EventEmitter } from '@angular/core';
import { Contacts } from 'src/models/Contacts';
import { HttpService } from './HttpService';
import { Messages } from 'src/models/Messages';
import { ThemesMessages } from 'src/models/ThemesMessages';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  providers: [HttpService]
})
export class AppComponent {
  title = 'ContactForm';
  contact: Contacts = new Contacts();
  code: Number = 10000 + Math.round(Math.random()*(99999-10000));
  entered_code = "";
  //messages: Messages[] = []
  //themesMessages: ThemesMessages[] = []
  //receivedUser?: Contacts // полученный пользователь
  message: Messages = new Messages();
  themeMessage: ThemesMessages = new ThemesMessages();
  contactUpdated = new EventEmitter<Contacts[]>();
  //messageUpdated = new EventEmitter<Messages[]>();
  // themeMessageUpdated = new EventEmitter<ThemesMessages[]>();

  constructor(private httpService: HttpService) {};

  //done: boolean = false

  //   submit(user: Contacts) {
  //   this.httpService.postData(user).subscribe(
  //     (data: any) => {
  //       this.receivedUser = data
  //       this.done = true
  //     },
  //     (error) => console.log(error)
  //   )
  // }

  submit(contact: Contacts) {
    if (parseInt(this.entered_code) == this.code){
      this.message.text = "test";
      contact.message = this.message;
      this.themeMessage.themeMessage = "test";
      contact.themes = this.themeMessage;
      console.log(contact);
      this.httpService
      .createContact(contact)
      .subscribe((contact_t: Contacts[]) => this.contactUpdated.emit(contact_t));
    }
    else{
      alert('Введенный вами код не совпадает!');
    }

    // this.httpService
    // .createMessage(message)
    // .subscribe((message_t) => this.messageUpdated.emit(message_t));

    // this.httpService
    // .createThemeMessage(themeMessage)
    // .subscribe((themeMessage_t) => this.themeMessageUpdated.emit(themeMessage_t));
  }
}
